import React, { useContext, useState } from 'react';
import { ProductsContext } from '../context/ProductsContext'; // Import the ProductsContext
import Header from './Header';

import {
  Card,
  CardContent,
  Button,
  TextField,
  CardActions,
  Typography,
  Grid,
} from '@mui/material';

function Menu() {
  const { products } = useContext(ProductsContext); // Use products from context
  const [searchTerm, setSearchTerm] = useState('');

  // Filter products based on the search term
  const filteredItems = products.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleOrder = (item) => {
    // Implement the order logic here
    // You can add the selected item to a cart or perform any other action.
    console.log("Ordering", item);
  };

  const cardStyle = {
    marginLeft: '25px', // Adjust the margin value as needed
  };

  const imageStyle = {
    maxWidth: '100%', // Limit the image width to the container's width
    maxHeight: '150px', // Limit the image height
    objectFit: 'cover', // Maintain the aspect ratio and cover the entire container
  };

  const searchInputStyle = {
    margin: '20px 0', // Add margin to the top and bottom of the search bar
  };

  return (
    <div>
      <Header />
      <TextField
        label="Search Food"
        variant="outlined"
        fullWidth
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={searchInputStyle}
      />
      <Grid container spacing={2} className="menu-items">
        {filteredItems.map((item) => (
          <Grid item key={item.id} xs={12} sm={6} md={4} lg={3}>
            <Card variant="outlined" className="menu-item-card" style={cardStyle}>
              <CardContent>
                <img
                  src={item.image_path || item.image} // Use image_path if it exists, otherwise fall back to image
                  alt={item.name}
                  style={imageStyle}
                  className="menu-item-image"
                />
                <Typography variant="h6">{item.name}</Typography>
                <Typography>{item.description}</Typography>
                <Typography>Price: ${item.price}</Typography>
              </CardContent>
              <CardActions>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => handleOrder(item)}
                >
                  Order
                </Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </div>
  );
}

export default Menu;
