import React, { useState, useEffect } from 'react';
import Header from './Header';

function CrudOperations() {
  const [action, setAction] = useState(null);
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    id: '',
    name: '',
    price: '',
    description: '',
    image_path: ''
  });
  const [currentProduct, setCurrentProduct] = useState({
    id: '',
    name: '',
    price: '',
    description: '',
    image_path: ''
  });

  useEffect(() => {
    if (action === 'see') {
      fetch('/api/products')
        .then(response => response.json())
        .then(data => setProducts(data))
        .catch(error => console.error('Error fetching products:', error));
    }
  }, [action]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (action === 'update') {
      setCurrentProduct({ ...currentProduct, [name]: value });
    } else {
      setProduct({ ...product, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('/api/products', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(product)
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product created:', data);
        setProducts([...products, data]);
        setProduct({ id: '', name: '', price: '', description: '', image_path: '' }); // Reset the form
      })
      .catch(error => console.error('Error creating product:', error));
  };

  const handleUpdateSubmit = (e) => {
    e.preventDefault();
    fetch(`/api/products/update/${currentProduct.name}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        price: currentProduct.price,
        description: currentProduct.description,
        // You may include other fields as needed
      })
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product updated:', data);
        setProducts(products.map(p => (p.id === currentProduct.id ? { ...p, ...data } : p)));
        setCurrentProduct({ id: '', name: '', price: '', description: '', image_path: '' }); // Reset the form
      })
      .catch(error => console.error('Error updating product:', error));
  };

  const handleDelete = (productName) => {
    fetch(`/api/products/delete/${productName}`, {
      method: 'DELETE'
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product deleted:', data);
        setProducts(products.filter(product => product.name !== productName));      })
      .catch(error => console.error('Error deleting product:', error));
  };

  const renderBox = () => {
    switch (action) {
      case 'insert':
        return (
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={product.name}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={product.price}
              onChange={handleInputChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={product.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="image_path"
              placeholder="Image Path"
              value={product.image_path}
              onChange={handleInputChange}
            />
            <button type="submit">Submit</button>
          </form>
        );
        case 'see':
      return (
        <div className="product-list">
          <h3>Product List</h3>
          {products.map((product, index) => (
            <div key={index} className="product-item">
              <p>ID: {product.id}</p>
              <p>Name: {product.name}</p>
              <p>Price: {product.price}</p>
              <p>Description: {product.description}</p>
              {product.image_path && (
                <img src={product.image_path} alt={product.name} className="product-image" />
              )}
              <div className="action-buttons">
                <button onClick={() => setCurrentProduct(product)} className="update-button" style={{ marginRight: '10px' }}>Update</button>
                <button onClick={() => handleDelete(product.name)} className="delete-button">Delete</button>
              </div>
            </div>
          ))}
        </div>
      );



      case 'update':
        return (
          <form onSubmit={handleUpdateSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={currentProduct.name}
              onChange={handleInputChange}

            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={currentProduct.price}
              onChange={handleInputChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={currentProduct.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="image_path"
              placeholder="Image Path"
              value={currentProduct.image_path}
              onChange={handleInputChange}
            />
            <button type="submit">Update Product</button>
          </form>
        );
      case 'delete':
        return <p>Select a product to delete from the list.</p>;
      default:
        return <div>Select an action</div>;
    }
  };

  return (
    <div>
        <Header />
    <div className="container">
      <div className="button-group">
        <button onClick={() => setAction('insert')}>Insert</button>
        <button onClick={() => setAction('see')}>See</button>
        <button onClick={() => setAction('update')}>Update</button>
        <button onClick={() => setAction('delete')}>Delete</button>
      </div>
      {renderBox()}
    </div>
    </div>
  );
  }

export default CrudOperations;
