import React, { useState, useEffect } from 'react';
import Header from './Header';

function CrudOperations() {
  const [action, setAction] = useState(null);
  const [products, setProducts] = useState([]);
  const [product, setProduct] = useState({
    id: '',
    name: '',
    price: '',
    description: '',
    image_path: ''
  });
  const [currentProduct, setCurrentProduct] = useState({
    id: '',
    name: '',
    price: '',
    description: '',
    image_path: ''
  });

  useEffect(() => {
    if (action === 'see') {
      fetch('/api/products')
        .then(response => response.json())
        .then(data => setProducts(data))
        .catch(error => console.error('Error fetching products:', error));
    }
  }, [action]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (action === 'update') {
      setCurrentProduct({ ...currentProduct, [name]: value });
    } else {
      setProduct({ ...product, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const productData = {
      name: product.name,
      price: product.price,
      description: product.description,
      image_path: product.image_path
    };

    fetch('/api/products', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(productData)
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product created:', data);
        setProducts([...products, data]);
      })
      .catch(error => console.error('Error creating product:', error));
  };

  const handleUpdateSubmit = (e) => {
    e.preventDefault();
    const productData = {
      price: currentProduct.price,
      description: currentProduct.description,
      image_path: currentProduct.image_path
    };

    fetch(`/api/products/update/${currentProduct.name}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(productData)
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product updated:', data);
        setProducts(products.map(p => (p.id === currentProduct.id ? { ...p, ...productData } : p)));
      })
      .catch(error => console.error('Error updating product:', error));
  };

  const handleDelete = (name) => {
    fetch(`/api/products/delete/${name}`, {
      method: 'DELETE'
    })
      .then(response => response.json())
      .then(data => {
        console.log('Product deleted:', data);
        setProducts(products.filter(p => p.name !== name));
      })
      .catch(error => console.error('Error deleting product:', error));
  };

  const renderBox = () => {
    switch (action) {
      case 'insert':
        return (
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={product.name}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={product.price}
              onChange={handleInputChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={product.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="image_path"
              placeholder="Image Path"
              value={product.image_path}
              onChange={handleInputChange}
            />
            <button type="submit">Submit</button>
          </form>
        );
      case 'see':
        return (
          <div>
            <h3>Product List</h3>
            {products.map((product, index) => (
              <div key={index}>
                <p>ID: {product.id}</p>
                <p>Name: {product.name}</p>
                <p>Price: {product.price}</p>
                <p>Description: {product.description}</p>
                {product.image_path && (
                  <img src={product.image_path} alt={product.name} style={{ width: '100px', height: '100px' }} />
                )}
                <button onClick={() => {
                  setCurrentProduct(product);
                  setAction('update');
                }}>Update</button>
                <button onClick={() => handleDelete(product.name)}>Delete</button>
              </div>
            ))}
          </div>
        );
      case 'update':
        return (
          <form onSubmit={handleUpdateSubmit}>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={currentProduct.name}
              onChange={handleInputChange}
              disabled
            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={currentProduct.price}
              onChange={handleInputChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={currentProduct.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="image_path"
              placeholder="Image Path"
              value={currentProduct.image_path}
              onChange={handleInputChange}
            />
            <button type="submit">Update Product</button>
          </form>
        );
      case 'delete':
        return <p>Select a product to delete from the list.</p>;
      default:
        return <div>Select an action</div>;
    }
  };

  return (
    <div>
      <Header />
      <button onClick={() => setAction('insert')}>Insert</button>
      <button onClick={() => setAction('see')}>See</button>
      <button onClick={() => setAction('update')}>Update</button>
      <button onClick={() => setAction('delete')}>Delete</button>
      {renderBox()}
    </div>
  );
}

export default CrudOperations;
