import React, { useState } from 'react';
import Header from './Header';

function CrudOperations() {
  const [action, setAction] = useState(null);
  const [product, setProduct] = useState({
    id: '',
    name: '',
    price: '',
    description: '',
    image: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProduct({ ...product, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would handle the submission, e.g., send to an API or state management
    console.log(product);
  };

  const renderBox = () => {
    switch (action) {
      case 'insert':
        return (
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="id"
              placeholder="ID"
              value={product.id}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={product.name}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="price"
              placeholder="Price"
              value={product.price}
              onChange={handleInputChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={product.description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="image"
              placeholder="Image URL"
              value={product.image}
              onChange={handleInputChange}
            />
            <button type="submit">Submit</button>
          </form>
        );
      // ... other cases for 'see', 'update', 'delete'
      default:
        return <div>Select an action</div>;
    }
  };

  return (
    <div>
      <button onClick={() => setAction('insert')}>Insert</button>
      <button onClick={() => setAction('see')}>See</button>
      <button onClick={() => setAction('update')}>Update</button>
      <button onClick={() => setAction('delete')}>Delete</button>
      {renderBox()}
    </div>
  );
}

export default CrudOperations;
