import React, { useRef, useEffect } from 'react';
import '../styles/styles.css'; // Assuming your CSS is in a file named styles.css

const LoginPage = () => {
  const containerRef = useRef(null);

  // Event handlers
  const handleRegisterClick = () => {
    containerRef.current.classList.add("active");
  };

  const handleLoginClick = () => {
    containerRef.current.classList.remove("active");
  };

  // Side effects to attach event listeners
  useEffect(() => {
    // Adding the event listeners
    const registerBtn = document.getElementById('register');
    const loginBtn = document.getElementById('login');
    if (registerBtn) {
      registerBtn.addEventListener('click', handleRegisterClick);
    }
    if (loginBtn) {
      loginBtn.addEventListener('click', handleLoginClick);
    }

    // Cleanup function to remove event listeners
    return () => {
      if (registerBtn) {
        registerBtn.removeEventListener('click', handleRegisterClick);
      }
      if (loginBtn) {
        loginBtn.removeEventListener('click', handleLoginClick);
      }
    };
  }, []);

  return (
    <div className="container" ref={containerRef}>
      <div className="toggle-container">
        <div className="toggle">
          <div className="toggle-panel toggle-left">
            <h1>Welcome Back!</h1>
            <p>To keep connected with us, please login with your personal info</p>
            <button className="ghost" id="login">Sign In</button>
            <form>
              {/* Add your login form elements here */}
              <input type="text" placeholder="Username" />
              <input type="password" placeholder="Password" />
              <button type="submit">Login</button>
            </form>
          </div>
          <div className="toggle-panel toggle-right">
            <h1>Hello, Friend!</h1>
            <p>Enter your personal details and start your journey with us</p>
            <button className="ghost" id="register">Sign Up</button>
            <form>
              {/* Add your registration form elements here */}
              <input type="text" placeholder="Username" />
              <input type="password" placeholder="Password" />
              <input type="password" placeholder="Confirm Password" />
              <button type="submit">Register</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
