// components/ManageProduct.js

import React, { useState } from 'react';
import ProductForm from './ProductForm1'; // This component would be your form for insert/update
import ProductList from './ProductList'; // This component would list products and have delete functionality
import Header from './Header';

function ManageProduct() {
  const [action, setAction] = useState(null);

  const handleActionChange = (newAction) => {
    setAction(newAction);
  };

  return (
    <div>
      <button onClick={() => handleActionChange('insert')}>Insert Product</button>
      <button onClick={() => handleActionChange('update')}>Update Product</button>
      <button onClick={() => handleActionChange('view')}>View Products</button>
      <button onClick={() => handleActionChange('delete')}>Delete Product</button>

      {action === 'insert' || action === 'update' ? <ProductForm action={action} /> : null}
      {action === 'view' || action === 'delete' ? <ProductList action={action} /> : null}
    </div>
  );
}

export default ManageProduct;
