import React, { useState } from 'react';
import Header from './Header';

function CrudOperations() {
  const [action, setAction] = useState(null);

  const renderBox = () => {
    switch (action) {
      case 'insert':
        return <div>Insert Box</div>;
      case 'see':
        return <div>See Box</div>;
      case 'update':
        return <div>Update Box</div>;
      case 'delete':
        return <div>Delete Box</div>;
      default:
        return <div>Select an action</div>;
    }
  };

  return (
    <div>
        <Header></Header>
      <button onClick={() => setAction('insert')}>Insert</button>
      <button onClick={() => setAction('see')}>See</button>
      <button onClick={() => setAction('update')}>Update</button>
      <button onClick={() => setAction('delete')}>Delete</button>
      {renderBox()}
    </div>
  );
}

export default CrudOperations;
